const getColor = (series, xAxis, yAxis, zAxis) => {
  const zColorScale = zAxis?.colorScale;
  if (zColorScale) {
    return dataIndex => {
      const value = series.data[dataIndex];
      const color = zColorScale(value[2]);
      if (color === null) {
        return '';
      }
      return color;
    };
  }
  return () => '';
};
export default getColor;